    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        this.ruta = this.detector.buscarImagen();
        
        if (!this.ruta.isEmpty()) {
            this.detector.mostrarImagen(this.ruta, this.lblImagenSistema);
        }
        
        
        
    }//GEN-LAST:event_btnBuscarActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        this.detector.detectarRostros(this.ruta, this.lblImagenDetectada);
        this.txtRuta.setText(this.ruta);
        this.txtRostros.setText(this.detector.getRostros() + "");
    }//GEN-LAST:event_jButton1ActionPerformed

